  const seq = buffer.readInt32BE();
  const body = schemas.ListResponse.decode(buffer.slice(8));

  return {
    result: body,
    seq,
  };
};

easySock.isReceiveComplete = (buffer) => {
  if (buffer.length < 8) {
    return;
  }
  const bodyLength = buffer.readInt32BE(4);
